INSERT INTO tale_dna (tale_id, tale_dna) VALUES
('grimm-024', '{"tale":{"taleId":"grimm-024","title":"Frau Holle","language":"de","age":{"min":4,"max":8},"summary":"Ein fleißiges Mädchen fällt in einen Brunnen und kommt zu Frau Holle. Fleiß wird belohnt, Faulheit bestraft.","moralLesson":"Fleiß und Freundlichkeit werden belohnt.","cultureRegion":"german","source":"grimm","themeTags":["diligence","kindness","justice"],"coreConflict":"Two sisters face the same challenge but respond with different attitudes.","emotionalArc":["curious","warm","industrious","triumphant","just"],"chapterCountHint":5,"iconicBeats":["Falling into the well","Shaking the apple tree and baking bread","Serving Frau Holle diligently","The golden rain reward","The lazy sister gets pitch"],"fixedElements":["well","Frau Holle","feather shaking","golden rain","pitch rain"],"flexibleElements":["artifact role","helper animals","additional tasks"],"toneBounds":{"targetTone":"Warm, fair, encouraging","contentRules":["Keep moral clear but gentle","No harsh punishment descriptions","Emphasize positive behavior","Use simple language"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["diligent","kind"],"constraints":["age=child"],"visualHints":["simple dress","kind smile"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["lazy","selfish"],"constraints":["age=child"],"visualHints":["messy appearance","frown"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":true,"roleCount":1,"archetypePreference":["wise","powerful"],"constraints":["age=elder"],"visualHints":["Frau Holle","white hair","feather bed"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["mentor"],"constraints":["age=adult"],"visualHints":["mother figure"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Am Brunnen","setting":"village well","mood":"COZY","sceneDescription":"Das fleißige Mädchen verliert die Spindel im Brunnen und springt hinein.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Die Aufgaben","setting":"magical meadow","mood":"MYSTERIOUS","sceneDescription":"Im Zauberland bittet ein Apfelbaum und ein Backofen um Hilfe.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Bei Frau Holle","setting":"Frau Holle cottage","mood":"COZY","sceneDescription":"Das Mädchen schüttelt fleißig die Betten und wird von Frau Holle gelobt.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der goldene Regen","setting":"gate between worlds","mood":"TRIUMPH","sceneDescription":"Das Mädchen wird mit goldenem Regen belohnt und kehrt nach Hause zurück.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Gerechtigkeit","setting":"village home","mood":"COZY","sceneDescription":"Die faule Schwester versucht es auch, wird aber mit Pech überschüttet.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]}]}')
INSERT INTO tale_dna (tale_id, tale_dna) VALUES
('grimm-083', '{"tale":{"taleId":"grimm-083","title":"Hans im Glück","language":"de","age":{"min":5,"max":9},"summary":"Hans tauscht seinen Goldklumpen gegen immer weniger wertvolle Dinge und endet glücklich mit leeren Händen.","moralLesson":"Wahres Glück liegt nicht in materiellem Besitz.","cultureRegion":"german","source":"grimm","themeTags":["happiness","simplicity","contentment"],"coreConflict":"Hans keeps trading away valuable possessions but finds joy in each exchange.","emotionalArc":["proud","cheerful","carefree","lighthearted","joyful"],"chapterCountHint":5,"iconicBeats":["Receiving gold for seven years of work","Trading gold for a horse","Trading horse for a cow then pig then goose","Trading goose for a grindstone","Dropping the stone and feeling free"],"fixedElements":["gold nugget","series of trades","country road","grindstone"],"flexibleElements":["trade partners","journey details","artifact twist"],"toneBounds":{"targetTone":"Lighthearted, humorous, philosophical","contentRules":["Keep tone cheerful throughout","No sadness about losses","Emphasize Hans happiness","Simple joyful language"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["innocent","cheerful"],"constraints":["age=young_adult"],"visualHints":["simple clothes","happy face","carrying items"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["trader","traveler"],"constraints":["age=adult"],"visualHints":["merchant","road traveler"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":false,"roleCount":1,"archetypePreference":["wise"],"constraints":["age=elder"],"visualHints":["old master"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Goldklumpen","setting":"masters house","mood":"COZY","sceneDescription":"Hans erhält seinen Lohn – einen großen Goldklumpen – und macht sich auf den Heimweg.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der erste Tausch","setting":"country road","mood":"FUNNY","sceneDescription":"Hans tauscht das schwere Gold gegen ein Pferd und reitet fröhlich weiter.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Tausch um Tausch","setting":"village market","mood":"FUNNY","sceneDescription":"Hans tauscht weiter: Pferd gegen Kuh, Kuh gegen Schwein, Schwein gegen Gans.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der letzte Tausch","setting":"roadside well","mood":"FUNNY","sceneDescription":"Hans tauscht die Gans gegen einen Schleifstein und lässt ihn in den Brunnen fallen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Das wahre Glück","setting":"open countryside","mood":"TRIUMPH","sceneDescription":"Hans tanzt frei und glücklich nach Hause – ohne Last, voller Freude.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}')
INSERT INTO tale_dna (tale_id, tale_dna) VALUES
('grimm-020', '{"tale":{"taleId":"grimm-020","title":"Das tapfere Schneiderlein","language":"de","age":{"min":5,"max":10},"summary":"Ein Schneider erschlägt sieben Fliegen auf einen Streich und wird durch List und Mut zum König.","moralLesson":"Cleverness and courage can overcome brute strength.","cultureRegion":"german","source":"grimm","themeTags":["courage","cleverness","adventure"],"coreConflict":"A small tailor must prove himself against giants and wild beasts through wit.","emotionalArc":["proud","adventurous","tense","clever","triumphant"],"chapterCountHint":5,"iconicBeats":["Seven at one blow","Meeting the giant","The squeezing contest","Catching the unicorn","Becoming king"],"fixedElements":["seven at one blow belt","giant","unicorn","castle"],"flexibleElements":["challenges","helper characters","artifact twist"],"toneBounds":{"targetTone":"Adventurous, humorous, empowering","contentRules":["Keep violence cartoonish","Emphasize cleverness over strength","Celebrate the underdog","Age-appropriate humor"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["clever","brave"],"constraints":["age=young_adult"],"visualHints":["small but confident","belt with motto","needle and thread"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["brute","giant"],"constraints":["size=large"],"visualHints":["giant","club","mountains"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":false,"roleCount":1,"archetypePreference":["royalty"],"constraints":["age=adult"],"visualHints":["king","crown","throne"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Sieben auf einen Streich","setting":"tailor workshop","mood":"FUNNY","sceneDescription":"Der kleine Schneider erschlägt sieben Fliegen und macht sich einen Gürtel mit der Aufschrift.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der Riese","setting":"mountain path","mood":"TENSE","sceneDescription":"Der Schneider trifft einen Riesen und fordert ihn mit einem Trick heraus.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Die Prüfungen","setting":"dark forest","mood":"TENSE","sceneDescription":"Der König stellt dem Schneider gefährliche Aufgaben: wilde Tiere fangen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Das Einhorn","setting":"enchanted forest","mood":"MYSTERIOUS","sceneDescription":"Der Schneider fängt das Einhorn mit einer cleveren List am Baum.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"König Schneiderlein","setting":"royal castle","mood":"TRIUMPH","sceneDescription":"Der tapfere Schneider wird zum König gekrönt und lebt glücklich.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]}]}')
INSERT INTO tale_dna (tale_id, tale_dna) VALUES
('grimm-036', '{"tale":{"taleId":"grimm-036","title":"Tischchen deck dich","language":"de","age":{"min":5,"max":9},"summary":"Drei Brüder erhalten magische Geschenke und müssen sie vor einem diebischen Wirt retten.","moralLesson":"Gerechtigkeit siegt und Betrug wird bestraft.","cultureRegion":"german","source":"grimm","themeTags":["magic","justice","family"],"coreConflict":"A thieving innkeeper steals the brothers magic gifts until the youngest retrieves them.","emotionalArc":["hopeful","excited","betrayed","determined","triumphant"],"chapterCountHint":5,"iconicBeats":["Learning the trade","Receiving the magic table","The innkeeper steals the gifts","The magic cudgel","Justice is restored"],"fixedElements":["magic table","gold donkey","cudgel from the sack","thieving innkeeper"],"flexibleElements":["trades learned","journey details","artifact twist"],"toneBounds":{"targetTone":"Adventurous, fair, satisfying","contentRules":["Keep violence slapstick","Emphasize family bonds","Clear moral about honesty","Satisfying justice ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["clever","determined"],"constraints":["age=young_adult"],"visualHints":["youngest brother","traveler clothes"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["greedy","thief"],"constraints":["age=adult"],"visualHints":["innkeeper","sly expression"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["craftsman"],"constraints":["age=adult"],"visualHints":["master craftsman","workshop"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die drei Brüder","setting":"family home","mood":"COZY","sceneDescription":"Drei Brüder ziehen in die Welt, um ein Handwerk zu lernen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Die magischen Geschenke","setting":"craftsman workshop","mood":"MYSTERIOUS","sceneDescription":"Jeder Bruder erhält ein magisches Geschenk von seinem Meister.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Der diebische Wirt","setting":"roadside inn","mood":"TENSE","sceneDescription":"Der Wirt stiehlt nachts die magischen Geschenke und tauscht sie aus.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Knüppel aus dem Sack","setting":"roadside inn","mood":"FUNNY","sceneDescription":"Der jüngste Bruder kommt mit dem Knüppel aus dem Sack und zwingt den Wirt zur Herausgabe.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Das Festmahl","setting":"family home","mood":"TRIUMPH","sceneDescription":"Die Brüder vereinen ihre Geschenke und feiern ein großes Fest.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}')
INSERT INTO tale_dna (tale_id, tale_dna) VALUES
('grimm-153', '{"tale":{"taleId":"grimm-153","title":"Die Sterntaler","language":"de","age":{"min":3,"max":7},"summary":"Ein armes Waisenkind gibt alles was es hat an noch Ärmere. Sterne regnen als Goldtaler vom Himmel.","moralLesson":"Großzügigkeit wird vom Universum belohnt.","cultureRegion":"german","source":"grimm","themeTags":["generosity","kindness","miracle"],"coreConflict":"A poor orphan gives away everything yet receives the greatest gift.","emotionalArc":["sad","compassionate","selfless","magical","joyful"],"chapterCountHint":5,"iconicBeats":["The orphan sets out alone","Giving away bread to a hungry child","Giving away clothing piece by piece","Standing alone in the dark forest","Stars fall as golden coins"],"fixedElements":["orphan child","starry night","golden coins from sky","acts of giving"],"flexibleElements":["people met","items given","artifact twist"],"toneBounds":{"targetTone":"Gentle, touching, magical","contentRules":["Keep sadness gentle","Emphasize kindness","Magical wonder at the end","Very simple language for young children"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["kind","selfless"],"constraints":["age=child"],"visualHints":["poor but kind child","simple clothes"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["needy","grateful"],"constraints":["age=child"],"visualHints":["hungry child","cold child"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Das arme Kind","setting":"empty village","mood":"COZY","sceneDescription":"Ein armes Waisenkind hat nur ein Stück Brot und die Kleider am Leib.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Das Brot","setting":"village road","mood":"COZY","sceneDescription":"Das Kind trifft einen hungrigen Mann und gibt ihm sein letztes Brot.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Alles hergeben","setting":"dark forest path","mood":"MYSTERIOUS","sceneDescription":"Nacheinander gibt das Kind Mütze, Jacke und Hemd an frierende Kinder.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Allein im Dunkeln","setting":"night forest clearing","mood":"MYSTERIOUS","sceneDescription":"Das Kind steht allein und frierend im Wald und schaut zum Sternenhimmel.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Sterntaler","setting":"starlit clearing","mood":"TRIUMPH","sceneDescription":"Die Sterne fallen als goldene Taler vom Himmel und das Kind ist reich und glücklich.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}')
INSERT INTO tale_dna (tale_id, tale_dna) VALUES
('grimm-033a', '{"tale":{"taleId":"grimm-033a","title":"Der gestiefelte Kater","language":"de","age":{"min":5,"max":10},"summary":"Ein cleverer Kater verhilft seinem armen Herrn durch List zu Reichtum und einer Prinzessin.","moralLesson":"Cleverness and loyalty can change your fortune.","cultureRegion":"german","source":"grimm","themeTags":["cleverness","loyalty","transformation"],"coreConflict":"A poor millers son must rise to nobility with only a clever cat as his ally.","emotionalArc":["hopeless","curious","adventurous","tense","triumphant"],"chapterCountHint":5,"iconicBeats":["Inheriting only a cat","The cat gets boots","Bringing gifts to the king","The swimming trick","Defeating the ogre"],"fixedElements":["cat with boots","poor millers son","king","ogre castle"],"flexibleElements":["gifts to king","journey details","artifact twist"],"toneBounds":{"targetTone":"Adventurous, witty, satisfying","contentRules":["Keep cat charming and clever","No real violence","Emphasize wit over force","Fun and engaging"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["humble","trusting"],"constraints":["age=young_adult"],"visualHints":["poor but handsome","simple clothes"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["clever","loyal"],"constraints":["species=animal"],"visualHints":["cat with boots","feathered hat","confident pose"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["powerful","shapeshifter"],"constraints":["size=large"],"visualHints":["ogre","dark castle"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":false,"roleCount":1,"archetypePreference":["royalty"],"constraints":["age=adult"],"visualHints":["king","crown"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Das Erbe","setting":"old mill","mood":"COZY","sceneDescription":"Der jüngste Sohn erbt nur einen Kater, der sich als außergewöhnlich klug erweist.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Geschenke für den König","setting":"royal palace","mood":"FUNNY","sceneDescription":"Der Kater bringt dem König Geschenke im Namen seines Herrn.","mustIncludeSlots":["SLOT_HELPER_1","SLOT_GUARDIAN_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Der Trick am Fluss","setting":"riverside","mood":"FUNNY","sceneDescription":"Der Kater inszeniert einen Überfall am Fluss und der König hilft dem vermeintlichen Grafen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1","SLOT_GUARDIAN_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Das Schloss des Ogers","setting":"dark castle","mood":"TENSE","sceneDescription":"Der Kater fordert den Oger heraus, sich in eine Maus zu verwandeln – und frisst ihn.","mustIncludeSlots":["SLOT_HELPER_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Graf und Prinzessin","setting":"royal castle","mood":"TRIUMPH","sceneDescription":"Der Müllersohn heiratet die Prinzessin und der Kater wird zum Hofkater ernannt.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1","SLOT_GUARDIAN_1"]}]}')
