INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('russian-001', '{"tale":{"taleId":"russian-001","title":"Wassilissa die Wunderschoene","language":"de","age":{"min":6,"max":10},"summary":"Wassilissa wird zu Baba Jaga geschickt. Mit Hilfe einer magischen Puppe besteht sie alle Pruefungen.","moralLesson":"Mut und Guete ueberwinden selbst die groesste Gefahr.","cultureRegion":"russian","source":"russian","themeTags":["courage","magic","kindness"],"coreConflict":"A kind girl must survive the terrifying trials of the witch Baba Yaga.","emotionalArc":["scared","brave","resourceful","tense","triumphant"],"chapterCountHint":5,"iconicBeats":["Receiving the magic doll from her mother","Being sent to Baba Yagas hut","The hut on chicken legs","Completing impossible tasks","Returning with magical fire"],"fixedElements":["magic doll","Baba Yagas hut on chicken legs","skull lantern","dark forest"],"flexibleElements":["tasks given","forest creatures","artifact twist"],"toneBounds":{"targetTone":"Dark fairy tale, brave, magical","contentRules":["Keep Baba Yaga scary but not terrifying","Emphasize bravery","Magic doll as comforting presence","Triumphant ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["brave","kind"],"constraints":["age=child"],"visualHints":["Russian dress","braided hair","magic doll"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["witch","powerful"],"constraints":["age=elder"],"visualHints":["Baba Yaga","iron teeth","mortar and pestle"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["magical","tiny"],"constraints":["size=tiny"],"visualHints":["small wooden doll","glowing eyes"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die magische Puppe","setting":"village cottage","mood":"COZY","sceneDescription":"Wassilissas sterbende Mutter gibt ihr eine magische Puppe als Schutz.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"In den Wald","setting":"dark birch forest","mood":"MYSTERIOUS","sceneDescription":"Die boese Stiefmutter schickt Wassilissa zu Baba Jaga um Feuer zu holen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Das Huehnerhaus","setting":"Baba Yagas hut","mood":"TENSE","sceneDescription":"Das Haus auf Huehnerbeinen dreht sich und Baba Jaga stellt unmoeglich Aufgaben.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die Pruefungen","setting":"Baba Yagas yard","mood":"TENSE","sceneDescription":"Mit Hilfe der Puppe besteht Wassilissa alle Pruefungen der Hexe.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Das magische Feuer","setting":"village path home","mood":"TRIUMPH","sceneDescription":"Wassilissa kehrt mit dem leuchtenden Schaedel zurueck und ist frei.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('russian-002', '{"tale":{"taleId":"russian-002","title":"Der Feuervogel","language":"de","age":{"min":5,"max":10},"summary":"Iwan findet eine goldene Feder des Feuervogels und besteht mit Hilfe des grauen Wolfs viele Abenteuer.","moralLesson":"Mut und treue Freundschaft fuehren zum Ziel.","cultureRegion":"russian","source":"russian","themeTags":["adventure","loyalty","magic"],"coreConflict":"A prince must capture the magical Firebird while navigating treachery and impossible quests.","emotionalArc":["curious","adventurous","tense","betrayed","triumphant"],"chapterCountHint":5,"iconicBeats":["Finding the golden feather","The Tsars quest","Meeting the grey wolf","Capturing the Firebird","The wolf saves Ivan"],"fixedElements":["golden feather","Firebird","grey wolf","golden apples","Tsar"],"flexibleElements":["quests","betrayals","artifact twist"],"toneBounds":{"targetTone":"Epic, adventurous, magical","contentRules":["Keep betrayals mild","Emphasize loyalty","Magical creatures","Heroic tone"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["brave","honest"],"constraints":["age=young_adult"],"visualHints":["Russian prince","traveling clothes","golden feather"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["wise","loyal"],"constraints":["species=animal"],"visualHints":["large grey wolf","intelligent eyes"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":false,"roleCount":1,"archetypePreference":["authority","demanding"],"constraints":["age=adult"],"visualHints":["Tsar","crown","throne"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die goldene Feder","setting":"royal garden","mood":"MYSTERIOUS","sceneDescription":"Iwan findet eine leuchtende goldene Feder im Garten des Zaren.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der Auftrag des Zaren","setting":"throne room","mood":"TENSE","sceneDescription":"Der Zar schickt Iwan los um den Feuervogel zu fangen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Der graue Wolf","setting":"dark forest crossroads","mood":"MYSTERIOUS","sceneDescription":"Der graue Wolf wird Iwans treuer Begleiter und traegt ihn durch die Welt.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der Feuervogel","setting":"enchanted garden at night","mood":"TENSE","sceneDescription":"Iwan schleicht sich in den Garten und faengt den leuchtenden Feuervogel.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Heimkehr","setting":"royal palace","mood":"TRIUMPH","sceneDescription":"Iwan kehrt triumphierend mit dem Feuervogel zurueck.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1","SLOT_GUARDIAN_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('russian-003', '{"tale":{"taleId":"russian-003","title":"Vaeterchen Frost","language":"de","age":{"min":4,"max":9},"summary":"Ein freundliches Maedchen wird im Winter ausgesetzt. Vaeterchen Frost prueft sie und belohnt ihre Guete.","moralLesson":"Freundlichkeit und Geduld werden belohnt.","cultureRegion":"russian","source":"russian","themeTags":["kindness","patience","justice"],"coreConflict":"A kind girl must endure bitter cold and pass Father Frosts test of character.","emotionalArc":["sad","cold","brave","warm","joyful"],"chapterCountHint":5,"iconicBeats":["The cruel stepmother","Abandoned in the frozen forest","Father Frost appears","Passing the test with kindness","Returning with treasures"],"fixedElements":["frozen forest","Father Frost","cruel stepmother","treasure chest","ice and snow"],"flexibleElements":["forest creatures","tasks","artifact twist"],"toneBounds":{"targetTone":"Wintry, kind, magical","contentRules":["Keep cold scenes not too harsh","Emphasize kindness","Magical winter atmosphere","Clear justice ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["kind","patient"],"constraints":["age=child"],"visualHints":["warm shawl","rosy cheeks","gentle smile"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":false,"roleCount":1,"archetypePreference":["cruel","selfish"],"constraints":["age=adult"],"visualHints":["stepmother","angry face"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":true,"roleCount":1,"archetypePreference":["powerful","testing"],"constraints":["age=elder"],"visualHints":["Father Frost","ice beard","blue robe","staff"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die boese Stiefmutter","setting":"peasant cottage","mood":"COZY","sceneDescription":"Ein liebes Maedchen leidet unter der boesen Stiefmutter.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Ausgesetzt im Wald","setting":"frozen winter forest","mood":"TENSE","sceneDescription":"Die Stiefmutter laesst das Maedchen im eiskalten Wald zurueck.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Vaeterchen Frost","setting":"snow-covered clearing","mood":"MYSTERIOUS","sceneDescription":"Vaeterchen Frost erscheint und fragt das frierende Maedchen ob ihr kalt sei.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die Pruefung","setting":"ice throne","mood":"MYSTERIOUS","sceneDescription":"Das Maedchen antwortet freundlich und besteht die Pruefung mit Guete.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Goldene Belohnung","setting":"village path","mood":"TRIUMPH","sceneDescription":"Vaeterchen Frost belohnt das Maedchen mit Schaetzen und warmer Kleidung.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('russian-004', '{"tale":{"taleId":"russian-004","title":"Der Kolobok","language":"de","age":{"min":3,"max":6},"summary":"Ein rundes Broetchen rollt davon und singt ein Lied. Es entkommt vielen Tieren, wird aber vom Fuchs ueberlistet.","moralLesson":"Uebermut und Leichtsinn koennen gefaehrlich werden.","cultureRegion":"russian","source":"russian","themeTags":["caution","cleverness","adventure"],"coreConflict":"A rolling bread bun escapes everyone but meets its match in the cunning fox.","emotionalArc":["cheerful","adventurous","proud","surprised","humbled"],"chapterCountHint":5,"iconicBeats":["Baked by grandmother","Rolling away singing","Escaping the hare and bear","Meeting the cunning fox","The foxs trick"],"fixedElements":["round bread bun","old couple","forest animals","singing","fox"],"flexibleElements":["animals met","song variations","artifact twist"],"toneBounds":{"targetTone":"Playful, rhythmic, cautionary","contentRules":["Keep it fun and musical","Repetitive song pattern","Mild cautionary ending","Very simple for toddlers"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["cheerful","adventurous"],"constraints":["size=small"],"visualHints":["round golden bun","happy face","rolling"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["cunning","sly"],"constraints":["species=animal"],"visualHints":["red fox","sly smile"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Frisch gebacken","setting":"peasant cottage kitchen","mood":"COZY","sceneDescription":"Grossmutter backt ein rundes Broetchen das zum Leben erwacht.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Davongerollt","setting":"village road","mood":"FUNNY","sceneDescription":"Der Kolobok rollt singend davon und entkommt dem Hasen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Immer weiter","setting":"forest path","mood":"FUNNY","sceneDescription":"Er entkommt dem Wolf und dem Baeren mit seinem cleveren Lied.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der schlaue Fuchs","setting":"forest clearing","mood":"TENSE","sceneDescription":"Der Fuchs lobt den Kolobok und bittet ihn naeher zu kommen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Die Lektion","setting":"forest clearing","mood":"COZY","sceneDescription":"Der Kolobok lernt dass Uebermut nicht immer klug ist.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('russian-005', '{"tale":{"taleId":"russian-005","title":"Die Ruebe","language":"de","age":{"min":3,"max":6},"summary":"Ein Opa pflanzt eine Ruebe die riesig waechst. Alle muessen helfen um sie herauszuziehen.","moralLesson":"Gemeinsam sind wir stark.","cultureRegion":"russian","source":"russian","themeTags":["teamwork","family","perseverance"],"coreConflict":"A giant turnip is stuck and only teamwork from everyone, even the smallest, can pull it out.","emotionalArc":["proud","struggling","hopeful","united","joyful"],"chapterCountHint":5,"iconicBeats":["Grandfather plants the turnip","It grows enormous","Pulling with grandmother","More helpers join","The tiny mouse makes the difference"],"fixedElements":["giant turnip","grandfather","chain of pullers","tiny mouse"],"flexibleElements":["helper characters","garden details","artifact twist"],"toneBounds":{"targetTone":"Simple, repetitive, joyful","contentRules":["Cumulative repetitive pattern","Perfect for very young children","Emphasize teamwork","Joyful simple ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["determined","leader"],"constraints":["age=elder"],"visualHints":["grandfather","farmer hat","garden"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["small","determined"],"constraints":["species=animal","size=tiny"],"visualHints":["tiny mouse","determined look"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die Saat","setting":"cottage garden","mood":"COZY","sceneDescription":"Grossvater pflanzt eine Ruebe die riesengross waechst.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Zu gross","setting":"garden turnip patch","mood":"FUNNY","sceneDescription":"Die Ruebe ist so gross dass Grossvater sie nicht allein ziehen kann.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Alle helfen","setting":"garden turnip patch","mood":"FUNNY","sceneDescription":"Grossmutter, Enkelin, Hund und Katze helfen - aber die Ruebe sitzt fest.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die kleine Maus","setting":"garden turnip patch","mood":"FUNNY","sceneDescription":"Die kleinste Maus haengt sich dran und endlich bewegt sich die Ruebe.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Das Festessen","setting":"cottage kitchen","mood":"TRIUMPH","sceneDescription":"Die riesige Ruebe wird herausgezogen und alle feiern zusammen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('russian-006', '{"tale":{"taleId":"russian-006","title":"Teremok","language":"de","age":{"min":3,"max":6},"summary":"Ein kleines Haus im Wald wird zum Zuhause fuer immer mehr Tiere, bis ein grosser Baer kommt.","moralLesson":"Gastfreundschaft ist wunderbar, aber alles hat seine Grenzen.","cultureRegion":"russian","source":"russian","themeTags":["hospitality","community","sharing"],"coreConflict":"More and more animals squeeze into a tiny house until it finally cannot hold them all.","emotionalArc":["curious","welcoming","crowded","chaotic","rebuilt"],"chapterCountHint":5,"iconicBeats":["Finding the little house","Mouse moves in","More animals arrive","The house gets very crowded","The bear sits on it"],"fixedElements":["small wooden house","forest animals","bear","rebuilding"],"flexibleElements":["which animals arrive","house details","artifact twist"],"toneBounds":{"targetTone":"Playful, cumulative, warm","contentRules":["Repetitive cumulative pattern","Each animal asks permission","Bear scene funny not scary","Happy rebuilding ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["hospitable","small"],"constraints":["species=animal"],"visualHints":["small mouse","friendly face"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":false,"roleCount":1,"archetypePreference":["large","clumsy"],"constraints":["species=animal","size=large"],"visualHints":["big bear","clumsy"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Das Haeuschen","setting":"forest clearing","mood":"COZY","sceneDescription":"Eine kleine Maus findet ein verlassenes Haeuschen im Wald und zieht ein.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Neue Mitbewohner","setting":"teremok house","mood":"FUNNY","sceneDescription":"Ein Frosch, ein Hase und ein Fuchs bitten um Einlass und ziehen ein.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Immer voller","setting":"teremok house","mood":"FUNNY","sceneDescription":"Immer mehr Tiere kommen und das Haus wird immer enger.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der Baer","setting":"teremok house","mood":"FUNNY","sceneDescription":"Ein riesiger Baer will auch einziehen und setzt sich aufs Dach.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Neu gebaut","setting":"forest clearing","mood":"TRIUMPH","sceneDescription":"Alle Tiere bauen gemeinsam ein groesseres schoeneres Haus.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('russian-007', '{"tale":{"taleId":"russian-007","title":"Baba Jaga","language":"de","age":{"min":5,"max":9},"summary":"Ein Maedchen wird von der boesen Stiefmutter zu Baba Jaga geschickt. Mit Hoeflichkeit und Geschenken entkommt sie.","moralLesson":"Freundlichkeit und Klugheit helfen in schwierigen Situationen.","cultureRegion":"russian","source":"russian","themeTags":["cleverness","kindness","courage"],"coreConflict":"A girl must outsmart the fearsome witch through kindness to her servants.","emotionalArc":["scared","clever","kind","tense","free"],"chapterCountHint":5,"iconicBeats":["Sent to the witch","The hut on chicken legs","Being kind to the cat and dog","The magic comb and towel","Escaping Baba Yaga"],"fixedElements":["Baba Yaga hut","chicken legs","magic comb","towel that becomes river","cat and dog helpers"],"flexibleElements":["escape tools","kind acts","artifact twist"],"toneBounds":{"targetTone":"Suspenseful, clever, rewarding","contentRules":["Baba Yaga scary but beatable","Kindness as the key weapon","Exciting chase scene","Safe happy ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["clever","kind"],"constraints":["age=child"],"visualHints":["simple dress","braids","brave eyes"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["witch","terrifying"],"constraints":["age=elder"],"visualHints":["Baba Yaga","iron nose","flying mortar"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["loyal","grateful"],"constraints":["species=animal"],"visualHints":["cat","dog","grateful animals"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Zu Baba Jaga geschickt","setting":"village cottage","mood":"TENSE","sceneDescription":"Die Stiefmutter schickt das Maedchen zu Baba Jaga und hofft sie nicht wiederzusehen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Das Haus auf Huehnerbeinen","setting":"dark forest edge","mood":"MYSTERIOUS","sceneDescription":"Das Maedchen findet das drehende Haus und tritt ein.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Freundliche Taten","setting":"Baba Yagas kitchen","mood":"TENSE","sceneDescription":"Das Maedchen gibt der Katze Milch und dem Hund Brot. Sie verraten ihr den Fluchtweg.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die Flucht","setting":"dark forest chase","mood":"TENSE","sceneDescription":"Baba Jaga jagt ihr nach. Der magische Kamm wird zum Wald, das Handtuch zum Fluss.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"In Sicherheit","setting":"village home","mood":"TRIUMPH","sceneDescription":"Das Maedchen entkommt und kehrt sicher nach Hause zurueck.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('russian-008', '{"tale":{"taleId":"russian-008","title":"Iwan Zarewitsch und der graue Wolf","language":"de","age":{"min":6,"max":11},"summary":"Prinz Iwan reitet aus um den Dieb der goldenen Aepfel zu finden. Mit dem magischen Wolf besteht er Abenteuer.","moralLesson":"Treue Freundschaft ist wertvoller als jeder Schatz.","cultureRegion":"russian","source":"russian","themeTags":["adventure","loyalty","magic"],"coreConflict":"A prince and his magical wolf companion must complete three impossible quests.","emotionalArc":["determined","adventurous","loyal","tense","triumphant"],"chapterCountHint":5,"iconicBeats":["The golden apples are stolen","Meeting the grey wolf","Quest for the Firebird","Quest for the golden horse","The wolfs final sacrifice"],"fixedElements":["golden apples","grey wolf","Firebird","golden horse","three quests"],"flexibleElements":["quest details","betrayals","artifact twist"],"toneBounds":{"targetTone":"Epic, loyal, adventurous","contentRules":["Keep quests exciting","Emphasize wolf-prince friendship","No graphic violence","Heroic satisfying ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["noble","brave"],"constraints":["age=young_adult"],"visualHints":["prince clothes","sword","determined look"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["wise","loyal"],"constraints":["species=animal"],"visualHints":["large grey wolf","intelligent eyes","majestic"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":false,"roleCount":1,"archetypePreference":["authority"],"constraints":["age=adult"],"visualHints":["Tsar father","golden crown"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die goldenen Aepfel","setting":"royal garden at night","mood":"MYSTERIOUS","sceneDescription":"Jede Nacht verschwinden goldene Aepfel aus dem Garten des Zaren.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der graue Wolf","setting":"forest crossroads","mood":"MYSTERIOUS","sceneDescription":"Iwan begegnet dem grauen Wolf der sein treuer Begleiter wird.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Drei Aufgaben","setting":"distant kingdoms","mood":"TENSE","sceneDescription":"Iwan und der Wolf muessen den Feuervogel, das goldene Pferd und eine Prinzessin finden.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Verrat und Rettung","setting":"forest ambush","mood":"TENSE","sceneDescription":"Iwans Brueder verraten ihn, doch der treue Wolf rettet ihn.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Heimkehr des Helden","setting":"royal palace","mood":"TRIUMPH","sceneDescription":"Iwan kehrt mit allen Schaetzen zurueck und wird zum Thronfolger.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1","SLOT_GUARDIAN_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('1001-001', '{"tale":{"taleId":"1001-001","title":"Aladin und die Wunderlampe","language":"de","age":{"min":5,"max":11},"summary":"Aladin findet eine magische Lampe mit einem maechtigen Dschinn. Er wird reich, muss aber gegen einen boesen Zauberer kaempfen.","moralLesson":"Wahre Staerke kommt von innen, nicht von Magie.","cultureRegion":"arabian","source":"1001-nights","themeTags":["adventure","magic","cleverness"],"coreConflict":"A street boy must use wit and the genies power to defeat an evil sorcerer.","emotionalArc":["poor","amazed","powerful","threatened","victorious"],"chapterCountHint":5,"iconicBeats":["Meeting the sorcerer","Entering the cave","Discovering the lamp and genie","Winning the princess","Defeating the sorcerer"],"fixedElements":["magic lamp","genie","cave of wonders","evil sorcerer","palace"],"flexibleElements":["wishes","palace details","artifact twist"],"toneBounds":{"targetTone":"Exotic, adventurous, thrilling","contentRules":["Keep sorcerer scary but beatable","Emphasize Aladins cleverness","Magical wonder","Triumphant ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["street_smart","brave"],"constraints":["age=young_adult"],"visualHints":["ragged clothes then rich","turban","quick smile"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["evil","powerful"],"constraints":["age=adult"],"visualHints":["dark sorcerer","staff","dark robes"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["powerful","loyal"],"constraints":["size=large"],"visualHints":["blue genie","smoke","golden bracelets"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Strassenjunge","setting":"Arabian marketplace","mood":"COZY","sceneDescription":"Aladin lebt als armer Strassenjunge im bunten Basar.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Die Hoehle","setting":"cave of wonders","mood":"MYSTERIOUS","sceneDescription":"Ein Zauberer fuehrt Aladin in eine magische Hoehle voller Schaetze.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Der Dschinn","setting":"underground chamber","mood":"MYSTERIOUS","sceneDescription":"Aladin reibt die alte Lampe und ein maechtiger Dschinn erscheint.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der Kampf","setting":"golden palace","mood":"TENSE","sceneDescription":"Der boese Zauberer stiehlt die Lampe und Aladin muss sie mit Klugheit zurueckgewinnen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Der weise Herrscher","setting":"palace throne room","mood":"TRIUMPH","sceneDescription":"Aladin besiegt den Zauberer und wird ein weiser und guetiger Herrscher.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('1001-002', '{"tale":{"taleId":"1001-002","title":"Ali Baba und die 40 Raeuber","language":"de","age":{"min":6,"max":11},"summary":"Ali Baba entdeckt die geheime Hoehle der Raeuber. Die kluge Morgiana rettet ihn vor ihrer Rache.","moralLesson":"Klugheit und Loyalitaet sind staerker als Gewalt.","cultureRegion":"arabian","source":"1001-nights","themeTags":["cleverness","loyalty","treasure"],"coreConflict":"A poor woodcutter discovers thieves treasure and must outsmart them to survive.","emotionalArc":["curious","amazed","threatened","clever","safe"],"chapterCountHint":5,"iconicBeats":["Discovering the cave","Sesame open up","The greedy brother","The thieves plot revenge","Morgianas clever tricks"],"fixedElements":["cave entrance","Sesam oeffne dich","forty thieves","oil jars","Morgiana"],"flexibleElements":["escape methods","treasure details","artifact twist"],"toneBounds":{"targetTone":"Thrilling, clever, satisfying","contentRules":["Keep thieves menacing but not graphic","Emphasize Morgianas brilliance","Exciting but safe","Justice ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["humble","honest"],"constraints":["age=adult"],"visualHints":["woodcutter","simple clothes","donkey"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["ruthless","leader"],"constraints":["age=adult"],"visualHints":["thief leader","curved sword","mask"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["clever","brave"],"constraints":["age=young_adult"],"visualHints":["Morgiana","clever eyes","determined"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Holzfaeller","setting":"desert forest road","mood":"COZY","sceneDescription":"Ali Baba sammelt Holz und beobachtet eine Raeuberbande vor einem Felsen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Sesam oeffne dich","setting":"magic cave","mood":"MYSTERIOUS","sceneDescription":"Ali Baba spricht das Zauberwort und betritt eine Hoehle voller Gold.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Die Raeuber kommen","setting":"Ali Babas house","mood":"TENSE","sceneDescription":"Die vierzig Raeuber erfahren vom Diebstahl und planen Rache.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Morgianas List","setting":"Ali Babas courtyard","mood":"TENSE","sceneDescription":"Die kluge Morgiana durchschaut die als Oelhaendler verkleideten Raeuber.","mustIncludeSlots":["SLOT_HELPER_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Freiheit","setting":"Ali Babas house","mood":"TRIUMPH","sceneDescription":"Morgiana besiegt die Raeuber und Ali Baba lebt in Frieden und Wohlstand.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('1001-003', '{"tale":{"taleId":"1001-003","title":"Sindbad der Seefahrer","language":"de","age":{"min":6,"max":11},"summary":"Sindbad erlebt sieben fantastische Seereisen voller Abenteuer mit Riesen und fliegenden Voegeln.","moralLesson":"Neugier und Mut fuehren zu den groessten Abenteuern.","cultureRegion":"arabian","source":"1001-nights","themeTags":["adventure","exploration","courage"],"coreConflict":"A merchant sailor faces incredible dangers on exotic islands and always finds a way to survive.","emotionalArc":["restless","amazed","terrified","resourceful","wise"],"chapterCountHint":5,"iconicBeats":["Setting sail","The whale island","The giant bird Roc","The valley of diamonds","Returning home wealthy and wise"],"fixedElements":["sailing ship","exotic islands","giant Roc bird","diamonds","sea monsters"],"flexibleElements":["island adventures","creatures met","artifact twist"],"toneBounds":{"targetTone":"Adventurous, exotic, thrilling","contentRules":["Keep monsters exciting not too scary","Emphasize Sindbads cleverness","Exotic locations","Wisdom from travels"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["adventurous","clever"],"constraints":["age=adult"],"visualHints":["sailor","turban","weathered face","travel bag"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["loyal","brave"],"constraints":["age=adult"],"visualHints":["fellow sailor","ship mate"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Sehnsucht nach dem Meer","setting":"busy port city","mood":"COZY","sceneDescription":"Sindbad kann dem Ruf des Meeres nicht widerstehen und sticht in See.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Die Walinsel","setting":"mysterious island","mood":"MYSTERIOUS","sceneDescription":"Die Mannschaft landet auf einer Insel die sich als riesiger Wal entpuppt.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Der Riesenvogel","setting":"tropical island","mood":"TENSE","sceneDescription":"Sindbad wird vom riesigen Vogel Roch in ein Tal voller Diamanten getragen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die groesste Gefahr","setting":"sea monster waters","mood":"TENSE","sceneDescription":"Sindbad muss mit all seinem Geschick einem Seeungeheuer entkommen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Heimkehr des Weisen","setting":"home port Baghdad","mood":"TRIUMPH","sceneDescription":"Sindbad kehrt reich und weise zurueck und erzaehlt seine Geschichten.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
