INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('andersen-001', '{"tale":{"taleId":"andersen-001","title":"Die kleine Meerjungfrau","language":"de","age":{"min":6,"max":10},"summary":"Eine junge Meerjungfrau verliebt sich in einen Prinzen und opfert ihre Stimme fuer Beine.","moralLesson":"Wahre Liebe bedeutet Opfer, aber man darf sich selbst nicht verlieren.","cultureRegion":"danish","source":"andersen","themeTags":["love","sacrifice","identity"],"coreConflict":"A mermaid must choose between her ocean home and the human world she longs for.","emotionalArc":["longing","brave","hopeful","heartbroken","transcendent"],"chapterCountHint":5,"iconicBeats":["Watching the prince from the sea","Visiting the sea witch","Walking on land for the first time","The princes ball","Choosing love over revenge"],"fixedElements":["ocean kingdom","sea witch","human legs","princes castle","sunrise deadline"],"flexibleElements":["sea creatures","artifact twist","underwater scenes"],"toneBounds":{"targetTone":"Poetic, emotional, hopeful","contentRules":["Soften the tragic elements for children","Emphasize courage and love","No graphic pain descriptions","Hopeful ending adaptation"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["dreamer","brave"],"constraints":["age=young_adult"],"visualHints":["mermaid tail","long flowing hair","ocean colors"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["trickster","dark_magic"],"constraints":["age=adult"],"visualHints":["sea witch","tentacles","dark cave"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":false,"roleCount":1,"archetypePreference":["royalty","kind"],"constraints":["age=young_adult"],"visualHints":["prince","castle","ship"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Unter dem Meer","setting":"underwater palace","mood":"COZY","sceneDescription":"Die kleine Meerjungfrau traeumt von der Welt ueber dem Wasser.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der Prinz","setting":"stormy sea surface","mood":"MYSTERIOUS","sceneDescription":"Sie rettet einen Prinzen vor dem Ertrinken und verliebt sich.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Die Meerhexe","setting":"dark underwater cave","mood":"TENSE","sceneDescription":"Die Meerhexe bietet einen Tausch an: Stimme gegen menschliche Beine.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"An Land","setting":"royal castle","mood":"TENSE","sceneDescription":"Die Meerjungfrau tanzt stumm im Schloss und hofft auf die Liebe des Prinzen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Ueber den Wellen","setting":"sunrise at sea","mood":"TRIUMPH","sceneDescription":"Die Meerjungfrau waehlt Guete ueber Rache und wird zu einem Geist der Luft.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('andersen-002', '{"tale":{"taleId":"andersen-002","title":"Das haessliche Entlein","language":"de","age":{"min":3,"max":8},"summary":"Ein vermeintlich haessliches Entlein wird verspottet, bis es entdeckt, dass es ein wunderschoener Schwan ist.","moralLesson":"Jeder hat verborgene Schoenheit und Staerke.","cultureRegion":"danish","source":"andersen","themeTags":["identity","perseverance","beauty"],"coreConflict":"A duckling endures rejection before discovering its true magnificent nature.","emotionalArc":["lonely","sad","determined","surprised","joyful"],"chapterCountHint":5,"iconicBeats":["Born different from siblings","Mocked and chased away","Surviving winter alone","Seeing swans on the lake","Discovering own reflection as a swan"],"fixedElements":["duck nest","farmyard animals","frozen lake","swan reflection"],"flexibleElements":["seasons","helper animals","artifact twist"],"toneBounds":{"targetTone":"Gentle, encouraging, triumphant","contentRules":["Keep bullying scenes mild","Emphasize inner strength","Joyful transformation","Simple comforting language"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["outcast","gentle"],"constraints":["species=animal","age=child"],"visualHints":["grey fluffy duckling","sad eyes"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":false,"roleCount":1,"archetypePreference":["bully","proud"],"constraints":["species=animal"],"visualHints":["farmyard animals","chickens"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Das seltsame Ei","setting":"duck pond nest","mood":"COZY","sceneDescription":"Ein grosses graues Kueken schluepft und sieht anders aus als alle anderen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Verspottet","setting":"farmyard","mood":"TENSE","sceneDescription":"Die anderen Tiere lachen und jagen das haessliche Entlein fort.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Allein im Winter","setting":"frozen lake","mood":"MYSTERIOUS","sceneDescription":"Das Entlein ueberlebt einen harten Winter ganz allein.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die Schwaene","setting":"spring lake","mood":"MYSTERIOUS","sceneDescription":"Das Entlein sieht praechtige Schwaene und wagt sich zu ihnen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Ein Schwan","setting":"sunlit lake","mood":"TRIUMPH","sceneDescription":"Im Wasser erkennt es sein Spiegelbild - es ist der schoenste Schwan von allen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('andersen-003', '{"tale":{"taleId":"andersen-003","title":"Die Schneekoenigin","language":"de","age":{"min":6,"max":11},"summary":"Kay wird von der Schneekoenigin entfuehrt. Seine Freundin Gerda reist ans Ende der Welt um ihn zu retten.","moralLesson":"Wahre Freundschaft und Liebe ueberwinden jede Kaelte.","cultureRegion":"danish","source":"andersen","themeTags":["friendship","courage","love"],"coreConflict":"A girl must cross treacherous lands to rescue her best friend from the Snow Queens icy spell.","emotionalArc":["warm","shocked","determined","brave","joyful"],"chapterCountHint":5,"iconicBeats":["The magic mirror shard hits Kay","Kay is taken by the Snow Queen","Gerda sets out alone","Gerda faces obstacles and finds allies","Gerdas tears melt Kays frozen heart"],"fixedElements":["magic mirror","ice palace","Snow Queen","frozen heart","warm tears"],"flexibleElements":["allies on journey","obstacles","artifact twist"],"toneBounds":{"targetTone":"Epic, warm, hopeful","contentRules":["Keep the Snow Queen mysterious not terrifying","Emphasize courage and love","No permanent harm","Triumphant warm ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["brave","loving"],"constraints":["age=child"],"visualHints":["warm clothes","red cheeks","determined look"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["cold","powerful"],"constraints":["age=adult"],"visualHints":["ice crown","white dress","snowflakes"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["friend","gentle"],"constraints":["age=child"],"visualHints":["boy with frozen heart","pale face"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Kay und Gerda","setting":"cozy town rooftop garden","mood":"COZY","sceneDescription":"Kay und Gerda sind beste Freunde und spielen in ihrem Dachgarten.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der Spiegelsplitter","setting":"snowy town square","mood":"TENSE","sceneDescription":"Ein Splitter des boesen Spiegels trifft Kays Herz und die Schneekoenigin entfuehrt ihn.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Gerdas Reise","setting":"enchanted lands","mood":"MYSTERIOUS","sceneDescription":"Gerda reist durch verzauberte Gaerten und kalte Laender auf der Suche nach Kay.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der Eispalast","setting":"ice palace","mood":"TENSE","sceneDescription":"Gerda findet Kay im Eispalast, gefangen in einem Raetsel aus Eisstuecken.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Warme Traenen","setting":"melting ice garden","mood":"TRIUMPH","sceneDescription":"Gerdas warme Traenen schmelzen den Splitter in Kays Herz und sie kehren nach Hause zurueck.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('andersen-004', '{"tale":{"taleId":"andersen-004","title":"Daeumelinchen","language":"de","age":{"min":4,"max":8},"summary":"Ein winziges Maedchen nicht groesser als ein Daumen erlebt viele Abenteuer bis sie ihren Platz bei den Blumenelfen findet.","moralLesson":"Jeder findet seinen Platz, egal wie klein.","cultureRegion":"danish","source":"andersen","themeTags":["adventure","belonging","kindness"],"coreConflict":"A tiny girl must escape danger after danger to find where she truly belongs.","emotionalArc":["wonder","scared","brave","hopeful","joyful"],"chapterCountHint":5,"iconicBeats":["Born from a flower","Kidnapped by a toad","Escaping with help","Helping the frozen swallow","Finding the flower prince"],"fixedElements":["tiny girl","flower birth","toad","swallow","flower kingdom"],"flexibleElements":["escape methods","helper creatures","artifact twist"],"toneBounds":{"targetTone":"Whimsical, gentle, magical","contentRules":["Keep dangers mild","Emphasize kindness to animals","Magical and enchanting tone","Simple language"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["tiny","kind"],"constraints":["age=child","size=tiny"],"visualHints":["tiny girl","flower dress","wings"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":false,"roleCount":1,"archetypePreference":["captor"],"constraints":["species=animal"],"visualHints":["toad","mole"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["loyal","grateful"],"constraints":["species=animal"],"visualHints":["swallow","blue feathers"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die Blume","setting":"cottage windowsill","mood":"COZY","sceneDescription":"Aus einer magischen Blume wird ein winziges Maedchen geboren.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Die K