INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('andersen-005', '{"tale":{"taleId":"andersen-005","title":"Des Kaisers neue Kleider","language":"de","age":{"min":5,"max":10},"summary":"Ein eitler Kaiser wird von zwei Betruegern ausgetrickst. Nur ein Kind sagt die Wahrheit.","moralLesson":"Sei mutig genug, die Wahrheit zu sagen.","cultureRegion":"danish","source":"andersen","themeTags":["truth","courage","vanity"],"coreConflict":"An entire kingdom pretends to see magical clothes until one child speaks the truth.","emotionalArc":["proud","excited","doubtful","tense","liberating"],"chapterCountHint":5,"iconicBeats":["The vain emperor","The swindlers arrive","Weaving invisible cloth","The grand procession","The child shouts the truth"],"fixedElements":["vain emperor","two swindlers","invisible clothes","grand procession","honest child"],"flexibleElements":["court reactions","artifact twist","procession details"],"toneBounds":{"targetTone":"Humorous, satirical, empowering","contentRules":["Keep humor gentle not mean","Emphasize courage to speak truth","Age-appropriate humor","Focus on the moral"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["honest","brave"],"constraints":["age=child"],"visualHints":["simple clothes","wide honest eyes"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["trickster","clever"],"constraints":["age=adult"],"visualHints":["swindler","flashy clothes","loom"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":true,"roleCount":1,"archetypePreference":["vain","powerful"],"constraints":["age=adult"],"visualHints":["emperor","crown","elaborate robes"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der eitle Kaiser","setting":"royal dressing room","mood":"FUNNY","sceneDescription":"Der Kaiser liebt praechtige Kleider ueber alles und gibt Vermoegen dafuer aus.","mustIncludeSlots":["SLOT_GUAR