INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('andersen-006', '{"tale":{"taleId":"andersen-006","title":"Die Prinzessin auf der Erbse","language":"de","age":{"min":3,"max":7},"summary":"Ein Prinz sucht eine echte Prinzessin. Eine Erbse unter zwanzig Matratzen stellt sie auf die Probe.","moralLesson":"Wahre Natur zeigt sich auch im Kleinsten.","cultureRegion":"danish","source":"andersen","themeTags":["authenticity","sensitivity","royalty"],"coreConflict":"A prince searches for a real princess and tests her with an impossible sensitivity test.","emotionalArc":["hopeful","stormy","curious","amusing","joyful"],"chapterCountHint":5,"iconicBeats":["The prince searches the world","A stranger arrives in a storm","The queen places the pea","A sleepless night","Revealing the real princess"],"fixedElements":["single pea","twenty mattresses","stormy night","royal castle"],"flexibleElements":["search journey","storm details","artifact twist"],"toneBounds":{"targetTone":"Lighthearted, cozy, amusing","contentRules":["Keep it simple and short","Gentle humor","Perfect for very young children","Warm ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["sensitive","genuine"],"constraints":["age=young_adult"],"visualHints":["princess","wet from rain","delicate"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":true,"roleCount":1,"archetypePreference":["wise","testing"],"constraints":["age=adult"],"visualHints":["queen","crown","knowing smile"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["romantic","hopeful"],"constraints":["age=young_adult"],"visualHints":["prince","castle"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die Suche","setting":"royal castle","mood":"COZY","sceneDescription":"Der Prinz reist durch die Welt auf der Suche nach einer echten Prinzessin.","mustIncludeSlots":["SLOT_HELPER_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Die Fremde im Sturm","setting":"castle gate in storm","mood":"MYSTERIOUS","sceneDescription":"In einer stuermischen Nacht klopft ein durchnaesstes Maedchen an die Tuer.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Die Erbse","setting":"royal bedroom","mood":"FUNNY","sceneDescription":"Die Koenigin legt eine Erbse unter zwanzig Matratzen als geheimen Test.","mustIncludeSlots":["SLOT_GUARDIAN_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die schlaflose Nacht","setting":"tower bedroom","mood":"FUNNY","sceneDescription":"Die Prinzessin kann kein Auge zumachen etwas Hartes stoert sie.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Echte Prinzessin","setting":"throne room","mood":"TRIUMPH","sceneDescription":"Sie ist eine echte Prinzessin. Der Prinz und sie heiraten gluecklich.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1","SLOT_GUARDIAN_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('andersen-007', '{"tale":{"taleId":"andersen-007","title":"Die Nachtigall","language":"de","age":{"min":5,"max":10},"summary":"Der Kaiser von China wird von einer echten Nachtigall verzaubert, ersetzt sie durch eine kuenstliche. Nur die echte rettet sein Leben.","moralLesson":"Echte Schoenheit kommt aus dem Herzen, nicht aus der Maschine.","cultureRegion":"danish","source":"andersen","themeTags":["nature","authenticity","gratitude"],"coreConflict":"An emperor must learn to value the real over the artificial before it is too late.","emotionalArc":["curious","enchanted","proud","desperate","grateful"],"chapterCountHint":5,"iconicBeats":["Hearing the nightingale sing","Bringing the bird to court","Receiving a jeweled mechanical bird","The real bird flies away","The nightingale saves the dying emperor"],"fixedElements":["real nightingale","mechanical bird","Chinese palace","emperor","forest"],"flexibleElements":["court reactions","journey to find bird","artifact twist"],"toneBounds":{"targetTone":"Poetic, wise, moving","contentRules":["Keep illness scene gentle","Emphasize nature over technology","Gratitude and humility","Beautiful imagery"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["powerful","learning"],"constraints":["age=adult"],"visualHints":["Chinese emperor","silk robes","jade crown"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["natural","pure"],"constraints":["species=animal"],"visualHints":["small brown nightingale","forest bird"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":false,"roleCount":1,"archetypePreference":["humble","guide"],"constraints":["age=child"],"visualHints":["kitchen maid","simple clothes"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Gesang im Wald","setting":"imperial forest","mood":"MYSTERIOUS","sceneDescription":"Der Kaiser hoert von einem wunderbaren Vogel in seinem eigenen Wald.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Die Nachtigall bei Hofe","setting":"imperial palace","mood":"COZY","sceneDescription":"Die echte Nachtigall singt am Hof und alle sind zu Traenen geruehrt.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Der mechanische Vogel","setting":"throne room","mood":"FUNNY","sceneDescription":"Ein juwelenbesetzter Kunstvogel ersetzt die echte Nachtigall die fortfliegt.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der kranke Kaiser","setting":"imperial bedroom","mood":"TENSE","sceneDescription":"Der Kaiser liegt im Sterben und der Kunstvogel ist kaputt.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Das Lied des Lebens","setting":"moonlit bedroom","mood":"TRIUMPH","sceneDescription":"Die echte Nachtigall kehrt zurueck und singt den Tod davon.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('andersen-008', '{"tale":{"taleId":"andersen-008","title":"Der standhafte Zinnsoldat","language":"de","age":{"min":4,"max":9},"summary":"Ein einbeiniger Zinnsoldat verliebt sich in eine Papiertaenzerin und erlebt viele Abenteuer.","moralLesson":"Standhaftigkeit und Treue ueberwinden alle Hindernisse.","cultureRegion":"danish","source":"andersen","themeTags":["perseverance","love","bravery"],"coreConflict":"A one-legged tin soldier endures incredible trials to return to the paper dancer he loves.","emotionalArc":["lonely","brave","terrified","determined","bittersweet"],"chapterCountHint":5,"iconicBeats":["Standing alone with one leg","Falling from the window","Riding a paper boat through the gutter","Swallowed by a fish","Returning to the dancing girl"],"fixedElements":["tin soldier","paper dancer","paper boat","rain gutter","fish"],"flexibleElements":["journey obstacles","helper figures","artifact twist"],"toneBounds":{"targetTone":"Brave, touching, adventurous","contentRules":["Keep the adventure exciting not scary","Emphasize bravery and loyalty","Gentle ending","Simple heroic language"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["steadfast","brave"],"constraints":["size=tiny"],"visualHints":["tin soldier","one leg","red uniform"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":false,"roleCount":1,"archetypePreference":["graceful","delicate"],"constraints":["size=tiny"],"visualHints":["paper ballerina","tutu","one leg pose"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Spielzeugtisch","setting":"childs nursery","mood":"COZY","sceneDescription":"Fuenfundzwanzig Zinnsoldaten stehen in einer Reihe. Einer hat nur ein Bein und verliebt sich in eine Papiertaenzerin.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Aus dem Fenster","setting":"window ledge","mood":"TENSE","sceneDescription":"Der Soldat faellt vom Fensterbrett auf die Strasse hinunter.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Die wilde Fahrt","setting":"rain gutter and canal","mood":"TENSE","sceneDescription":"In einem Papierboot faehrt der Soldat durch den Rinnstein in den Kanal.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Im Bauch des Fisches","setting":"inside a fish","mood":"MYSTERIOUS","sceneDescription":"Ein grosser Fisch verschluckt den Soldaten. Er steht standhaft im Dunkeln.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Wieder vereint","setting":"childs nursery","mood":"TRIUMPH","sceneDescription":"Der Fisch wird gefangen und der Soldat kehrt zur Taenzerin zurueck.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('andersen-016', '{"tale":{"taleId":"andersen-016","title":"Das Maedchen mit den Schwefelhoelzern","language":"de","age":{"min":6,"max":10},"summary":"Ein armes Maedchen versucht in der Silvesternacht Schwefelhoelzer zu verkaufen und hat wunderschoene Visionen.","moralLesson":"Mitgefuehl und Guete fuer die Schwaechsten der Gesellschaft.","cultureRegion":"danish","source":"andersen","themeTags":["compassion","hope","dreams"],"coreConflict":"A freezing girl finds warmth and beauty in her imagination while the world ignores her.","emotionalArc":["cold","hopeful","warm","beautiful","bittersweet"],"chapterCountHint":5,"iconicBeats":["Selling matches in the cold","First match lights a warm stove","Second match shows a feast","Third match shows a Christmas tree","Grandmother appears and takes her to heaven"],"fixedElements":["match girl","winter night","match flames","visions","grandmother"],"flexibleElements":["visions content","passersby","artifact twist"],"toneBounds":{"targetTone":"Gentle, poetic, hopeful","contentRules":["Adapt ending to be hopeful for children","Emphasize warmth and love","No graphic suffering","Focus on beautiful visions"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["vulnerable","dreamer"],"constraints":["age=child"],"visualHints":["thin dress","bare feet","matches"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":false,"roleCount":1,"archetypePreference":["loving","angelic"],"constraints":["age=elder"],"visualHints":["kind grandmother","warm light","gentle smile"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die kalte Nacht","setting":"snowy city street","mood":"COZY","sceneDescription":"Ein armes Maedchen versucht in der Kaelte Schwefelhoelzer zu verkaufen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Das erste Streichholz","setting":"dark alley corner","mood":"MYSTERIOUS","sceneDescription":"Sie zuendet ein Streichholz an und sieht einen warmen Ofen in der Flamme.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Wunderschoene Visionen","setting":"magical flame visions","mood":"MYSTERIOUS","sceneDescription":"Jedes Streichholz zeigt eine wundervolle Vision: ein Festmahl, ein Weihnachtsbaum.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die Grossmutter","setting":"heavenly light","mood":"COZY","sceneDescription":"Im letzten Licht erscheint ihre geliebte Grossmutter und streckt die Arme aus.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"In der Waerme","setting":"starlit sky","mood":"TRIUMPH","sceneDescription":"Die Grossmutter nimmt das Maedchen mit in eine Welt voller Waerme und Licht.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
