INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('lit-001', '{"tale":{"taleId":"lit-001","title":"Alice im Wunderland","language":"de","age":{"min":8,"max":12},"summary":"Alice folgt einem weißen Kaninchen in einen Kaninchenbau und erlebt fantastische Abenteuer in einer verrückten Welt voller skurriler Charaktere.","moralLesson":"Neugier führt zu wunderbaren Abenteuern","cultureRegion":"England","source":"literature","themeTags":["Fantasy","Abenteuer","Logik"],"coreConflict":"A girl must navigate a nonsensical world to find her way home.","emotionalArc":["curious","confused","amazed","frustrated","triumphant"],"chapterCountHint":8,"iconicBeats":["Down the rabbit hole","Drink me Eat me","The Cheshire Cat","The Mad Tea Party","The Queen of Hearts croquet","The Trial"],"fixedElements":["rabbit hole","white rabbit","pocket watch","Cheshire cat","Queen of Hearts"],"flexibleElements":["absurd riddles","tea party guests","size changes"],"toneBounds":{"targetTone":"Whimsical, absurd, dreamlike","contentRules":["Emphasize nonsense logic","Keep peril surreal not scary","Focus on wordplay","Dream ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["curious","polite"],"constraints":["age=child"],"visualHints":["blue dress","blond hair","white apron"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["tyrant","loud"],"constraints":["age=adult"],"visualHints":["red heart dress","crown","scepter"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["mysterious","guide"],"constraints":["species=animal"],"visualHints":["grinning cat","striped fur","floating"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Das weiße Kaninchen","setting":"riverbank","mood":"COZY","sceneDescription":"Alice folgt dem weißen Kaninchen in den Bau.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der Fall","setting":"rabbit hole","mood":"MYSTERIOUS","sceneDescription":"Alice fällt langsam tief in die Erde.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Die Teeparty","setting":"mad tea party garden","mood":"FUNNY","sceneDescription":"Alice trifft den Hutmacher und den Hasen zu einer verrückten Teeparty.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die Herzkönigin","setting":"royal garden","mood":"TENSE","sceneDescription":"Alice spielt Krocket mit der Königin und muss ihren Kopf behalten.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Das Erwachen","setting":"riverbank","mood":"TRIUMPH","sceneDescription":"Alice wacht auf und merkt, dass alles nur ein Traum war.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('lit-002', '{"tale":{"taleId":"lit-002","title":"Peter Pan","language":"de","age":{"min":7,"max":12},"summary":"Peter Pan, der Junge der nie erwachsen wird, nimmt Wendy und ihre Brüder mit nach Nimmerland.","moralLesson":"Die Magie der Kindheit bewahren","cultureRegion":"England","source":"literature","themeTags":["Fantasy","Abenteuer","Freundschaft"],"coreConflict":"A boy who refuses to grow up battles a pirate captain who wants revenge.","emotionalArc":["wonder","joyful","tense","brave","bittersweet"],"chapterCountHint":8,"iconicBeats":["Flying to Neverland","The Lost Boys hideout","Mermaid Lagoon","Tiger Lily rescue","Duel with Captain Hook"],"fixedElements":["flying","Tinkerbell","Captain Hook","ticking crocodile","Neverland"],"flexibleElements":["pirate crew","island locations","adventures"],"toneBounds":{"targetTone":"Magical, adventurous, spirited","contentRules":["Flying feels magical","Hook is scary but funny","Emphasize childhood joy","Bittersweet ending for Wendy"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["adventurous","flying"],"constraints":["age=child"],"visualHints":["green tunic","feather in hat","flying"]},{"slotKey":"SLOT_PROTAGONIST_2","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["caring","motherly"],"constraints":["age=child"],"visualHints":["nightgown","hair ribbon"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["pirate","villain"],"constraints":["age=adult"],"visualHints":["hook hand","pirate hat","red coat"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["fairy","jealous"],"constraints":["size=tiny"],"visualHints":["glowing fairy","wings","wand"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Schatten","setting":"nursery","mood":"MAGICAL","sceneDescription":"Peter verliert seinen Schatten im Kinderzimmer und Wendy näht ihn an.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_PROTAGONIST_2"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Flug nach Nimmerland","setting":"night sky over London","mood":"TRIUMPH","sceneDescription":"Sie fliegen zum zweiten Stern rechts und geradeaus bis zum Morgen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_PROTAGONIST_2","SLOT_HELPER_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Die Piraten","setting":"Skull Rock","mood":"TENSE","sceneDescription":"Peter kämpft gegen Hook um Tiger Lily zu retten.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Kampf auf dem Schiff","setting":"Jolly Roger deck","mood":"TENSE","sceneDescription":"Der finale Kampf zwischen Peter und Hook auf dem Piratenschiff.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Heimkehr","setting":"nursery window","mood":"BITTERSWEET","sceneDescription":"Wendy kehrt heim, aber Peter fliegt zurück nach Nimmerland.","mustIncludeSlots":["SLOT_PROTAGONIST_2","SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('lit-003', '{"tale":{"taleId":"lit-003","title":"Pinocchio","language":"de","age":{"min":6,"max":10},"summary":"Eine Holzpuppe erwacht zum Leben und muss lernen, ehrlich und brav zu sein, um ein echter Junge zu werden.","moralLesson":"Ehrlichkeit und gutes Verhalten machen uns zu echten Menschen","cultureRegion":"Italien","source":"literature","themeTags":["Fantasy","Moral","Transformation"],"coreConflict":"A puppet must overcome his naivety and dishonesty to become real.","emotionalArc":["naive","mischievous","scared","ashamed","brave"],"chapterCountHint":8,"iconicBeats":["Geppetto carves Pinocchio","The cricket advice","Fox and Cat trickery","Growing a donkey tail","Inside the whale"],"fixedElements":["growing nose","Jiminy Cricket","Blue Fairy","Whale","Donkey ears"],"flexibleElements":["temptations","adventures","puppet show"],"toneBounds":{"targetTone":"Moralistic, magical, redemption","contentRules":["Lying consequences clear","Scary scenes managed","Focus on father-son bond","Redemption ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["naive","learning"],"constraints":["age=child"],"visualHints":["wooden puppet","long nose","colorful clothes"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["conscience","wise"],"constraints":["species=animal"],"visualHints":["cricket","top hat","umbrella"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":true,"roleCount":1,"archetypePreference":["kind","fatherly"],"constraints":["age=elder"],"visualHints":["woodcarver","apron","glasses"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die lebendige Puppe","setting":"woodcarver shop","mood":"COZY","sceneDescription":"Geppetto schnitzt Pinocchio, der plötzlich lebendig wird.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der Holzweg","setting":"village street","mood":"FUNNY","sceneDescription":"Pinocchio verkauft seine Fibel für das Theater statt zur Schule zu gehen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Lügen haben lange Nasen","setting":"Blue Fairy cottage","mood":"SHAME","sceneDescription":"Pinocchio lügt die Fee an und seine Nase wächst.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Im Bauch des Wals","setting":"inside whale belly","mood":"DRAMATIC","sceneDescription":"Pinocchio findet Geppetto im Bauch des Wals und rettet ihn.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Ein echter Junge","setting":"Geppetto home","mood":"TRIUMPH","sceneDescription":"Pinocchio wird für seinen Mut belohnt und wird ein echter Junge.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('lit-004', '{"tale":{"taleId":"lit-004","title":"Das Dschungelbuch","language":"de","age":{"min":8,"max":12},"summary":"Der Menschenjunge Mogli wächst bei Wölfen im Dschungel auf und lernt die Gesetze der Natur.","moralLesson":"Respekt vor der Natur und ihren Gesetzen","cultureRegion":"Indien","source":"literature","themeTags":["Abenteuer","Tiere","Natur"],"coreConflict":"A man-cub caught between the jungle world and the human village, hunted by a tiger.","emotionalArc":["wild","playful","fearful","brave","mature"],"chapterCountHint":8,"iconicBeats":["Found by Bagheera","Baloo teaches the bare necessities","Monkeys kidnap Mowgli","Kaa hypnotizes","Shere Khan battle"],"fixedElements":["Mowgli","Baloo","Bagheera","Shere Khan","Wolf pack"],"flexibleElements":["monkey city","snake encounter","human village"],"toneBounds":{"targetTone":"Wild, adventurous, rhythmic","contentRules":["Jungle laws are important","Tiger is dangerous","Bear is fun","Respect for nature"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["wild","brave"],"constraints":["age=child"],"visualHints":["red loincloth","dark hair","barefoot"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["fun","strong"],"constraints":["species=animal"],"visualHints":["sloth bear","happy face"]},{"slotKey":"SLOT_HELPER_2","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["wise","serious"],"constraints":["species=animal"],"visualHints":["black panther","sleek"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["cruel","powerful"],"constraints":["species=animal"],"visualHints":["tiger","stripes","scars"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Das Findelkind","setting":"wolf den","mood":"COZY","sceneDescription":"Mogli wird vom Wolfsrudel aufgenomme