INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('lit-005', '{"tale":{"taleId":"lit-005","title":"Heidi","language":"de","age":{"min":7,"max":12},"summary":"Das Waisenmädchen Heidi heilt Herzen mit ihrer Fröhlichkeit, sowohl auf der Alm als auch in der Stadt.","moralLesson":"Naturverbundenheit und Herzlichkeit heilen","cultureRegion":"Schweiz","source":"literature","themeTags":["Realismus","Natur","Familie"],"coreConflict":"A nature-loving girl is torn away from her mountain home to a stifling city.","emotionalArc":["joyful","homesick","caring","depressed","radiant"],"chapterCountHint":8,"iconicBeats":["Meeting Grandfather","Goats with Peter","Taken to Frankfurt","The rolling chair miracle","Return to the mountains"],"fixedElements":["Grandfather","Peter","Goats","Frankfurt","Wheelchair"],"flexibleElements":["mountain adventures","city lessons","nature descriptions"],"toneBounds":{"targetTone":"Wholesome, fresh, touching","contentRules":["Nature heals","Homesickness is real pain","Kindness overcomes strictness","Miracle ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["joyful","nature_loving"],"constraints":["age=child"],"visualHints":["braids","simple dress","rosy cheeks"]},{"slotKey":"SLOT_GUARDIAN_1","roleType":"GUARDIAN","required":true,"roleCount":1,"archetypePreference":["grumpy_to_kind","old"],"constraints":["age=elder"],"visualHints":["white beard","pipe","mountain clothes"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["simple","loyal"],"constraints":["age=child"],"visualHints":["goatherd","ragged clothes"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Almöhi","setting":"mountain hut","mood":"COZY","sceneDescription":"Heidi zieht zu ihrem mürrischen Großvater und gewinnt sein Herz.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_GUARDIAN_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Ziegenpet