INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('lit-006', '{"tale":{"taleId":"lit-006","title":"Die Schatzinsel","language":"de","age":{"min":10,"max":14},"summary":"Jim Hawkins findet eine Schatzkarte und segelt zur geheimnisvollen Insel, verfolgt von Piraten.","moralLesson":"Mut und Cleverness besiegen Verrat","cultureRegion":"England","source":"literature","themeTags":["Abenteuer","Piraten","Mut"],"coreConflict":"A boy must grow up quickly to outwit a cunning pirate mutiny.","emotionalArc":["excited","suspicious","threatened","brave","mature"],"chapterCountHint":8,"iconicBeats":["Finding the map","The apple barrel","Mutiny","The stockade battle","Jim meets Ben Gunn","Finding the empty treasure pit"],"fixedElements":["map","Long John Silver","parrot","Hispaniola","buried treasure"],"flexibleElements":["island traps","pirate battles","sea journey"],"toneBounds":{"targetTone":"Gritty, adventurous, suspenseful","contentRules":["Pirate threat is real","Silver is complex villain","Action but no gore","Coming of age"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["brave","observant"],"constraints":["age=young_adult"],"visualHints":["cabin boy clothes","map","pistol"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["charismatic","duplicitous"],"constraints":["age=adult","disability=one_leg"],"visualHints":["crutch","parrot on shoulder","pirate coat"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["noble","capable"],"constraints":["age=adult"],"visualHints":["doctor coat","sword","wig"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die Karte","setting":"inn","mood":"MYSTERIOUS","sceneDescription":"Jim findet die Karte in der Truhe des alten Seemanns.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Das Apfelfass","settin