INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('legend-001', '{"tale":{"taleId":"legend-001","title":"Robin Hood","language":"de","age":{"min":9,"max":14},"summary":"Robin Hood stiehlt von den Reichen und gibt den Armen, während er den bösen Sheriff bekämpft.","moralLesson":"Für Gerechtigkeit einzustehen ist richtig","cultureRegion":"England","source":"legend","themeTags":["Abenteuer","Gerechtigkeit","Helden"],"coreConflict":"An outlaw fights a corrupt system to help the suffering people.","emotionalArc":["bold","righteous","romantic","daring","victorious"],"chapterCountHint":8,"iconicBeats":["Robin meets Little John","Robbing the rich","Archery contest","Rescue from the gallows","King Richard returns"],"fixedElements":["bow and arrow","Sherwood Forest","Sheriff","Maid Marian","Merry Men"],"flexibleElements":["tricks played","forest details","king''s return"],"toneBounds":{"targetTone":"Heroic, swashbuckling, fair","contentRules":["Justified outlaw behavior","Skill over strength","Camaraderie","Justice prevails"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["heroic","skilled"],"constraints":["age=adult"],"visualHints":["green hood","bow","feather"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["corrupt","greedy"],"constraints":["age=adult"],"visualHints":["sheriff badge","expensive clothes","sneer"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["strong","loyal"],"constraints":["age=adult"],"visualHints":["giant staff","tunic"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Im Sherwood","setting":"forest camp","mood":"COZY","sceneDescription":"Robin und seine Männer feiern in ihrem sicheren Waldlager.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der Überfall","setting":"forest road","mood":"AC