INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('legend-002', '{"tale":{"taleId":"legend-002","title":"König Artus","language":"de","age":{"min":10,"max":15},"summary":"Artus zieht das Schwert aus dem Stein und gründet die Tafelrunde für Gerechtigkeit und Frieden.","moralLesson":"Wahre Größe kommt von innen, nicht von Geburt","cultureRegion":"England","source":"legend","themeTags":["Fantasy","Ritter","Magie"],"coreConflict":"A boy must accept his destiny as King and unite a divided land.","emotionalArc":["humble","destined","noble","betrayed","legendary"],"chapterCountHint":8,"iconicBeats":["Sword in the stone","Lady of the Lake","The Round Table","Merlin''s magic","The final battle"],"fixedElements":["Excalibur","Merlin","Round Table","Camelot","Grail"],"flexibleElements":["knight quests","battles","magic spells"],"toneBounds":{"targetTone":"Epic, noble, legendary","contentRules":["Chivalry focus","Magic is mysterious","Destiny theme","Tragic but legendary ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["noble","destined"],"constraints":["age=young_adult"],"visualHints":["crown","cape","Excalibur"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["wise","mysterious"],"constraints":["age=elder"],"visualHints":["wizard robes","staff","beard"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["traitor","dark"],"constraints":["age=adult"],"visualHints":["dark armor","evil smile"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Stein","setting":"churchyard","mood":"MYSTERIOUS","sceneDescription":"Das Schwert steckt fest im Stein, niemand kann es ziehen.","mustIncludeSlots":["SLOT_HELPER_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der wahre König","setting":"churchyard","mood":"WONDER","sceneDescription":"Der junge Artus zieht das Schwert mühelos heraus.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Die Tafelrunde","setting":"Camelot hall","mood":"NOBLE","sceneDescription":"Artus versammelt die besten Ritter an der runden Tafel.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die letzte Schlacht","setting":"Camlann field","mood":"EPIC","sceneDescription":"Artus kämpft gegen den Verräter Mordred um sein Königreich.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Avalon","setting":"lake shoe","mood":"LEGENDARY","sceneDescription":"Artus wird nach Avalon gebracht um eines Tages wiederzukehren.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('legend-003', '{"tale":{"taleId":"legend-003","title":"Der Rattenfänger von Hameln","language":"de","age":{"min":8,"max":12},"summary":"Ein Pfeifer befreit die Stadt von Ratten, doch als er nicht bezahlt wird, entführt er die Kinder.","moralLesson":"Halte immer deine Versprechen","cultureRegion":"Deutschland","source":"legend","themeTags":["Legende","Moral","Gerechtigkeit"],"coreConflict":"A town tries to cheat a magician and pays a terrible price.","emotionalArc":["desperate","relieved","arrogant","terrified","tragic"],"chapterCountHint":8,"iconicBeats":["Rats everywhere","The Piper appears","Rats drown in river","Town refuses payment","Children follow the piper"],"fixedElements":["pied piper","flute","rats","mountain cave","broken promise"],"flexibleElements":["rat chaos scenes","townsfolk reactions"],"toneBounds":{"targetTone":"Mysterious, cautionary, dark","contentRules":["Consequences are severe","Music is magic","Honor agreements","Haunting ending"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["mysterious","powerful"],"constraints":["age=adult"],"visualHints":["colorful clothes","flute","feather hat"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["greedy","arrogant"],"constraints":["age=adult"],"visualHints":["mayor chains","fat","coins"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die Plage","setting":"town square","mood":"CHAOTIC","sceneDescription":"Die Stadt wird von tausenden Ratten überrannt.","mustIncludeSlots":["SLOT_ANTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Das Versprechen","setting":"town hall","mood":"HOPEFUL","sceneDescription":"Der Rattenfänger bietet Hilfe an gegen 1000 Goldstücke.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Der Betrug","setting":"town hall","mood":"ANGER","sceneDescription":"Die Stadt ist frei, aber der Bürgermeister will nicht zahlen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die Rache","setting":"streets","mood":"HAUNTING","sceneDescription":"Der Pfeifer spielt eine neue Melodie und alle Kinder folgen ihm.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Stille","setting":"mountain face","mood":"SAD","sceneDescription":"Der Berg schließt sich hinter den Kindern. Die Stadt bleibt still zurück.","mustIncludeSlots":["SLOT_ANTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('legend-004', '{"tale":{"taleId":"legend-004","title":"Till Eulenspiegel","language":"de","age":{"min":8,"max":12},"summary":"Der Narr Till Eulenspiegel hält den Menschen durch seine Streiche einen Spiegel vor.","moralLesson":"Cleverness und Wortwitz entlarven Dummheit","cultureRegion":"Deutschland","source":"legend","themeTags":["Humor","Schalk","Weisheit"],"coreConflict":"A prankster exposes the hypocrisy and stupidity of authority figures.","emotionalArc":["mischievous","clever","funny","chaotic","satisfied"],"chapterCountHint":8,"iconicBeats":["Baking owls and monkeys","Cutting the cloth","Flying from the roof","Buying all the milk","Teaching the donkey to read"],"fixedElements":["jester hat","mirror","owl","literal interpretations"],"flexibleElements":["specific pranks","locations"],"toneBounds":{"targetTone":"Humorous, witty, satirical","contentRules":["Pranks expose character flaws","Wordplay is key","Authority figures look foolish","Lighthearted fun"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["trickster","witty"],"constraints":["age=adult"],"visualHints":["jester cap","bells","mocking smile"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["pompous","foolish"],"constraints":["age=adult"],"visualHints":["rich clothes","angry face"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Schalk","setting":"marketplace","mood":"FUNNY","sceneDescription":"Till beobachtet die Eitelkeit der Leute und plant einen Streich.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Die Wette","setting":"tavern","mood":"FUNNY","sceneDescription":"Till wettet, er könne einem Esel das Lesen beibringen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Bäckerstreich","setting":"bakery","mood":"CHAOTIC","sceneDescription":"Till backt Eulen und Meerkatzen statt Brötchen, wie befohlen.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die Predigt","setting":"church square","mood":"FUNNY","sceneDescription":"Till verspricht zu fliegen und lacht die Menge aus, die ihm geglaubt hat.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Weiterziehen","setting":"road","mood":"SATISFIED","sceneDescription":"Till zieht lachend weiter zur nächsten Stadt, den Spiegel in der Hand.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('legend-005', '{"tale":{"taleId":"legend-005","title":"Die Loreley","language":"de","age":{"min":10,"max":14},"summary":"Eine Nixe auf dem Felsen betört Schiffer mit ihrem Gesang, bis sie zerschellen.","moralLesson":"Schönheit kann gefährlich sein","cultureRegion":"Deutschland","source":"legend","themeTags":["Legende","Romantik","Tragik"],"coreConflict":"Succumbing to dangerous beauty leads to ruin.","emotionalArc":["beautiful","enchanted","obsessed","doomed","melancholy"],"chapterCountHint":8,"iconicBeats":["Combing golden hair","Singing the song","Sailor looks up","Ship hits rocks","Eternal song"],"fixedElements":["Loreley rock","golden comb","Rhine river","shipwreck","siren song"],"flexibleElements":["sailor''s background","time of day"],"toneBounds":{"targetTone":"Poetic, tragic, haunting","contentRules":["Atmosphere is key","Song is hypnotic","Tragedy is inevitable","Romantic sadness"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["beautiful","dangerous"],"constraints":["species=magical"],"visualHints":["golden hair","rock","river"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["doomed","enchanted"],"constraints":["age=young_adult"],"visualHints":["sailor","boat","dazed look"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Felsen","setting":"Rhine river","mood":"SERENE","sceneDescription":"Der mächtige Felsen ragt über dem ruhigen Fluss auf.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Das Goldene Haar","setting":"rock top","mood":"BEAUTIFUL","sceneDescription":"Loreley beginnt ihr goldenes Haar zu kämmen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Der Gesang","setting":"river","mood":"HYPNOTIC","sceneDescription":"Ihr wunderbarer Gesang verzaubert einen vorbeifahrenden Schiffer.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der Aufprall","setting":"rapids","mood":"TRAGIC","sceneDescription":"Der Schiffer achtet nicht auf die Felsen und sein Boot zerschellt.","mustIncludeSlots":["SLOT_HELPER_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Ewiges Lied","setting":"sunset river","mood":"MELANCHOLY","sceneDescription":"Die Loreley singt weiter, unberührt vom Unglück.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('aesop-001', '{"tale":{"taleId":"aesop-001","title":"Der Fuchs und die Trauben","language":"de","age":{"min":5,"max":8},"summary":"Ein Fuchs kann leckere Trauben nicht erreichen und behauptet dann einfach, sie seien sauer.","moralLesson":"Wer etwas nicht erreichen kann, redet es schlecht","cultureRegion":"Antike","source":"aesop","themeTags":["Fabel","Tiere","Moral"],"coreConflict":"A fox wants what he cannot have and rationalizes his failure.","emotionalArc":["hungry","determined","frustrated","arrogant","dismissive"],"chapterCountHint":5,"iconicBeats":["Spotting the grapes","Jumping once","Jumping twice","Giving up","Calling them sour"],"fixedElements":["fox","vineyard","high grapes","sour remark"],"flexibleElements":["jumping attempts","spectators"],"toneBounds":{"targetTone":"Simple, humorous, moral","contentRules":["Simple clear action","Focus on the excuse","Classic fable format","Short and punchy"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["proud","hungry"],"constraints":["species=animal"],"visualHints":["red fox","jumping","snout"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Der Hunger","setting":"dusty road","mood":"COZY","sceneDescription":"Ein hungriger Fuchs sucht nach Futter.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Die Trauben","setting":"vineyard","mood":"DESIRE","sceneDescription":"Er sieht köstliche Trauben hoch oben hängen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Die Sprünge","setting":"vineyard","mood":"EFFORT","sceneDescription":"Er springt und springt, kommt aber nicht heran.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Die Aufgabe","setting":"vineyard","mood":"FRUSTRATION","sceneDescription":"Erschöpft muss er einsehen, dass er sie nicht kriegt.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Saure Trauben","setting":"vineyard exit","mood":"ARROGANT","sceneDescription":"Er geht weg und sagt: Die waren bestimmt sauer.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('aesop-002', '{"tale":{"taleId":"aesop-002","title":"Die Schildkröte und der Hase","language":"de","age":{"min":5,"max":8},"summary":"Beim Wettrennen gewinnt die langsame Schildkröte gegen den schnellen aber faulen Hasen.","moralLesson":"Langsam und stetig gewinnt das Rennen","cultureRegion":"Antike","source":"aesop","themeTags":["Fabel","Tiere","Ausdauer"],"coreConflict":"Speed vs Endurance in a race.","emotionalArc":["mocked","determined","steady","shocked","victorious"],"chapterCountHint":5,"iconicBeats":["The challenge","The start","Hare sleeps","Tortoise passes","Tortoise wins"],"fixedElements":["race","sleeping hare","steady tortoise","finish line"],"flexibleElements":["animal spectators","race route"],"toneBounds":{"targetTone":"Simple, encouraging, just","contentRules":["Clear contrast between fast/lazy and slow/steady","No cheating","Fair win","Classic moral"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["slow","steady"],"constraints":["species=animal"],"visualHints":["tortoise","shell","determined"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["fast","arrogant"],"constraints":["species=animal"],"visualHints":["hare","long ears","sleeping"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Die Wette","setting":"meadow","mood":"FUNNY","sceneDescription":"Der Hase verspottet die Schildkröte, die ihn zum Rennen herausfordert.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der Start","setting":"start line","mood":"EXCITED","sceneDescription":"Der Hase schießt davon, die Schildkröte startet langsam.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Das Schläfchen","setting":"under a tree","mood":"RELAXED","sceneDescription":"Der Hase ist so weit voraus, dass er ein Nickerchen macht.","mustIncludeSlots":["SLOT_ANTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Überholt","setting":"race path","mood":"QUIET","sceneDescription":"Leise zieht die Schildkröte am schlafenden Hasen vorbei.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Der Sieg","setting":"finish line","mood":"TRIUMPH","sceneDescription":"Die Schildkröte erreicht das Ziel, kurz bevor der Hase ankommt.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('aesop-003', '{"tale":{"taleId":"aesop-003","title":"Der Löwe und die Maus","language":"de","age":{"min":5,"max":8},"summary":"Eine Maus rettet einen Löwen aus einem Netz, nachdem er sie verschont hatte.","moralLesson":"Auch Kleine können Großen helfen","cultureRegion":"Antike","source":"aesop","themeTags":["Fabel","Tiere","Freundlichkeit"],"coreConflict":"A tiny creature must save a giant predator.","emotionalArc":["terrified","grateful","desperate","brave","friendly"],"chapterCountHint":5,"iconicBeats":["Mouse wakes Lion","Lion spares Mouse","Lion caught in net","Mouse gnaws ropes","Friends forever"],"fixedElements":["lion paw","hunter''s net","gnawing ropes","lifelong friendship"],"flexibleElements":["jungle setting","how lion is caught"],"toneBounds":{"targetTone":"Gentle, heroic, heartwarming","contentRules":["Lion is scary then grateful","Mouse is brave","Kindness repaid","Net trap clearly shown"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["tiny","brave"],"constraints":["species=animal"],"visualHints":["mouse","grey fur","sharp teeth"]},{"slotKey":"SLOT_HELPER_1","roleType":"HELPER","required":true,"roleCount":1,"archetypePreference":["huge","royal"],"constraints":["species=animal"],"visualHints":["lion","mane","big paws"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Gefangen","setting":"lion den","mood":"SCARY","sceneDescription":"Die Maus läuft dem Löwen über die Pfote und er fängt sie.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Das Versprechen","setting":"lion den","mood":"MERCY","sceneDescription":"Der Löwe lacht über das Hilfsangebot der Maus, lässt sie aber frei.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Das Netz","setting":"jungle trap","mood":"DESPAIR","sceneDescription":"Der Löwe wird in einem Jägernetz gefangen und brüllt um Hilfe.","mustIncludeSlots":["SLOT_HELPER_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Nagearbeit","setting":"jungle trap","mood":"BUSY","sceneDescription":"Die Maus kommt und nagt die dicken Seile durch.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Freunde","setting":"jungle","mood":"Warm","sceneDescription":"Der Löwe bedankt sich und erkennt die Maus als Freund an.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_HELPER_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
INSERT INTO tale_dna (tale_id, tale_dna) VALUES ('aesop-004', '{"tale":{"taleId":"aesop-004","title":"Der Hirtenjunge und der Wolf","language":"de","age":{"min":6,"max":9},"summary":"Ein Junge ruft zum Spaß ''Wolf!'', doch als der Wolf wirklich kommt, glaubt ihm niemand mehr.","moralLesson":"Lügen haben kurze Beine","cultureRegion":"Antike","source":"aesop","themeTags":["Fabel","Moral","Ehrlichkeit"],"coreConflict":"A bored boy cries wolf too many times and destroys his credibility.","emotionalArc":["bored","mischievous","gleeful","panicked","regretful"],"chapterCountHint":5,"iconicBeats":["Bored watching sheep","First false alarm","Villagers angry","Wolf actually comes","Nobody comes to help"],"fixedElements":["sheep","boy crying wolf","angry villagers","real wolf"],"flexibleElements":["number of false alarms","fate of sheep"],"toneBounds":{"targetTone":"Cautionary, dramatic, clear","contentRules":["Lying has consequences","Trust is easily broken","Wolf is real threat","Regret at end"]}},"roles":[{"slotKey":"SLOT_PROTAGONIST_1","roleType":"PROTAGONIST","required":true,"roleCount":1,"archetypePreference":["bored","dishonest"],"constraints":["age=child"],"visualHints":["shepherd staff","sheep","hill"]},{"slotKey":"SLOT_ANTAGONIST_1","roleType":"ANTAGONIST","required":true,"roleCount":1,"archetypePreference":["dangerous","real"],"constraints":["species=animal"],"visualHints":["wolf","teeth","shadow"]}],"scenes":[{"sceneNumber":1,"beatType":"SETUP","sceneTitle":"Langeweile","setting":"hillside","mood":"BORED","sceneDescription":"Dem Hirtenjungen ist langweilig beim Schafehüten.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":2,"beatType":"INCITING","sceneTitle":"Der erste Spaß","setting":"village edge","mood":"FUNNY","sceneDescription":"Er ruft ''Wolf!'' und lacht als die Dorfleute umsonst angerannt kommen.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]},{"sceneNumber":3,"beatType":"CONFLICT","sceneTitle":"Noch mal!","setting":"hillside","mood":"ARROGANT","sceneDescription":"Er macht es wieder und die Leute werden wütend.","mustIncludeSlots":["SLOT_PROTAGONIST_1"],"artifactPolicy":{"requiresArtifact":true,"artifactSlotKey":"SLOT_ARTIFACT_1","artifactMustBeVisible":true}},{"sceneNumber":4,"beatType":"CLIMAX","sceneTitle":"Der echte Wolf","setting":"twilight hill","mood":"TERROR","sceneDescription":"Ein echter Wolf kommt! Der Junge schreit, aber niemand kommt.","mustIncludeSlots":["SLOT_PROTAGONIST_1","SLOT_ANTAGONIST_1"]},{"sceneNumber":5,"beatType":"RESOLUTION","sceneTitle":"Die Lehre","setting":"village","mood":"SAD","sceneDescription":"Die Schafe sind weg. Der Junge hat gelernt: Wer lügt, dem glaubt man nicht.","mustIncludeSlots":["SLOT_PROTAGONIST_1"]}]}') ON CONFLICT (tale_id) DO UPDATE SET tale_dna = EXCLUDED.tale_dna;
